import React from "react";

class Eventhandling extends React.Component {
    constructor() {
        super();
        this.state = {
            nama: 'Geraldine', 
            count: 0,
            isLoggedIn: false, // State untuk menyimpan status login
            username: '', // State untuk menyimpan username
            password: '' // State untuk menyimpan password
        };
        this.tombolDiklik = this.tombolDiklik.bind(this);
        this.teksDiubah = this.teksDiubah.bind(this);
        this.handleLogin = this.handleLogin.bind(this); // Bind fungsi handleLogin
        this.handleLogout = this.handleLogout.bind(this); // Bind fungsi handleLogout
    }
    
    kirimpesan(nama) {
        alert('Kirim Pesan Kepada ' + nama);
    }

    ubahtulisan(e) {
        e.target.innerHTML = 'Kirim Pesan';
    }

    ubahlagi(e) {
        e.target.innerHTML = 'Click Me!!';
    }

    sapa(e) {
        this.setState({nama : e.target.value});
    }
    
    tombolDiklik() {
        console.log('Tombol Diklik');
        this.setState(prevState => ({count: prevState.count + 1}));
    }

    teksDiubah(e) {
        console.log('Teks diubah menjadi: ', e.target.value);
        this.setState({nama: e.target.value});
    }

    handleLogin() {
        const { username, password } = this.state;
        if (username === '' && password === '') {
            this.setState({ isLoggedIn: true });
            alert('Login berhasil!');
        } else {
            alert('Login gagal. Silakan coba lagi.');
        }
    }

    handleLogout() {
        this.setState({
            isLoggedIn: false,
            username: '',
            password: ''
        });
        alert('Logout berhasil!');
    }

    render() {
        const { isLoggedIn } = this.state;

        if (!isLoggedIn) {
            return (
                <div style={{textAlign: 'center'}}>
                    <h1>Halaman Login</h1>
                    <input 
                        type="text" 
                        placeholder="Username" 
                        onChange={e => this.setState({ username: e.target.value })}
                    /><br />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        onChange={e => this.setState({ password: e.target.value })}
                    /><br />
                    <button onClick={this.handleLogin}>Login</button>
                </div>
            );
        }

        return (
            <div style={{textAlign: 'center'}}>
                <button onClick={() => alert('hallowwww semua!!ðŸš€ðŸš€')}>Clik Me!!!</button>
                <h1> Aku Putri Sihombing</h1>
                <button onClick={this.kirimpesan.bind(this, 'Geraldine')}>Kirim pesan</button>
                <h1> Nama Panjang ku Putri Geraldine Alexsandra Sihombing</h1>
                <button onClick={this.kirimpesan.bind(this, 'Alexsandra')} onMouseEnter={(e) => this.ubahtulisan(e)} onMouseOut={(e) => this.ubahlagi(e)}>Tekan Aku!!!</button>
                <h1>Hallow : {this.state.nama}</h1>
                <input type={'text'} onChange={this.sapa.bind(this)}></input>
                <h1>Senang belajar disini</h1>
                <button onClick={this.tombolDiklik}>Klik Saya!!</button>
                <div>
                    <h1>Selamat datang, {this.state.nama}!</h1>
                    <button onClick={this.handleLogout}>Logout</button>
                </div>
            </div>
        );
    }
}

export default Eventhandling;
