import React from "react";

class Eventhandling extends React.Component {
    constructor() {
        super();
        this.state = {
            nama: 'Geraldine'
        }
    }

    kirimpesan(nama) {
        alert('Kirim Pesan Kepada ' + nama);
    }

    ubahtulisan(e) {
        e.target.innerHTML = 'Kirim Pesan';
    }

    ubahlagi(e) {
        e.target.innerHTML = 'Click Me!!'
    }

    sapa(e) {
        this.setState({ nama: e.target.value })
    }

    // Fungsi untuk menampilkan pesan saat tombol diklik
    tombolDiklik() {
        console.log('Tombol diklik');
    }

    // Fungsi untuk menampilkan pesan saat teks diubah
    teksDiubah(e) {
        console.log('Teks diubah menjadi:', e.target.value);
    }

    render() {
        return (
            <div>
                {/* Button yang akan merubah teks saat dihover dan saat mouse keluar */}
                <button onClick={this.kirimpesan.bind(this, 'Alexsandra')} onMouseEnter={(e) => this.ubahtulisan(e)} onMouseOut={this.ubahlagi.bind(this)}>Tekan Aku!!!</button>
                {/* Element untuk menampilkan salam dengan nama */}
                <h1>Hallow : {this.state.nama}</h1>
                {/* Input yang akan mengubah state nama */}
                <input type={'text'} onChange={this.sapa.bind(this)} ></input>
                {/* Button yang akan menampilkan pesan di console saat diklik */}
                <button onClick={this.tombolDiklik}>Klik Saya!</button>
                {/* Input yang akan menampilkan pesan di console saat teksnya diubah */}
                <input type="text" onChange={this.teksDiubah} />
            </div>
        )
    }
}

export default Eventhandling;
