import React from "react";


class Eventhandling extends React.Component{
    constructor(){
        super();
        this.state={
            nama : 'Geraldine', 
            count: 0
        }
        this.tombolDiklik = this.tombolDiklik.bind(this);
        this.teksDiubah = this.teksDiubah.bind(this);
    }
    kirimpesan(nama){
        alert('Kirim Pesan Kepada ' + nama);
    }

    ubahtulisan(e){
        e.target.innerHTML = 'Kirim Pesan';
    }

    ubahlagi(e){
        e.target.innerHTML = 'Click Me!!'
    }

    sapa(e){
        this.setState({nama : e.target.value})
    }
    
    tombolDiklik(){
        console.log('Tombol Diklik');
        this.setState(prevState=> ({count: prevState.count + 1}))
    }

    teksdiubah(e){
        console.log('Teks diubah menjadi: ', e.target.value);
        this.setState({nama: e.target.value});
    }


    ender() {
        return (
            <div style={{ textAlign: 'center' }}>
                <h1>Halo Semua!! ðŸš€ðŸš€</h1>
                <button onClick={() => alert('Halo Semua!! ðŸš€ðŸš€')}>Klik Saya!!!</button>
                <hr />
                <h2>Ayo Kenalan:</h2>
                <p>Nama saya adalah: {this.state.nama}</p>
                <input type="text" onChange={this.sapa.bind(this)} placeholder="Masukkan nama Anda" />
                <hr />
                <h2>Pesan Spesial:</h2>
                <button onClick={this.kirimpesan.bind(this, 'Geraldine')}>Kirim Pesan</button>
                <button onClick={this.kirimpesan.bind(this, 'Alexsandra')} onMouseEnter={(e) => this.ubahtulisan(e)} onMouseOut={this.ubahlagi.bind(this)}>Tekan Aku!!!</button>
                <hr />
                <h2>Hitung Berapa Kali Tombol Ini Sudah Diklik:</h2>
                <p>{this.state.count}</p>
                <button onClick={this.tombolDiklik}>Klik Saya!!</button>
                <hr />
                <h2>Bagaimana Perasaan Anda Hari Ini?</h2>
                <input type="text" onChange={this.teksDiubah} placeholder="Ceritakan perasaan Anda" />
            </div>
        )
    }
}

export default Eventhandling;
